mapper.select:
/* FAQ 리스트 조회 */ SELECT FAQ_PAGE.ROWNUMBER , FAQ_PAGE.FAQ_SN , FAQ_PAGE.FAQ_CTGRY , FAQ_PAGE.FAQ_QSTN , FAQ_PAGE.FAQ_ANS , FAQ_PAGE.SORT_NO FROM ( /* 순번 매기기 */ SELECT ROWNUM ROWNUMBER , FAQ.* FROM ( /* 리스트 조회 */ SELECT FAQ_SN , FAQ_CTGRY , FAQ_QSTN , FAQ_ANS , SORT_NO FROM KB_FAQ_INFO WHERE 1=1
AND (FAQ_QSTN LIKE '%'|| #{keyword} ||'%' OR FAQ_ANS LIKE '%'|| #{keyword} ||'%')
AND FAQ_QSTN LIKE '%'|| #{keyword} ||'%'
AND FAQ_ANS LIKE '%'|| #{keyword} ||'%'
AND FAQ_CTGRY = #{ctgry}
ORDER BY FAQ_SN ASC ) FAQ ORDER BY ROWNUMBER DESC ) FAQ_PAGE WHERE ROWNUMBER BETWEEN #{start} AND #{end}

mapper.insert:
 /* FAQ 등록 */
 INSERT INTO KB_FAQ_INFO
 (
  FAQ_SN
, FAQ_CTGRY
, FAQ_QSTN
, FAQ_ANS
, SORT_NO
, EXPSR_YN
, FRST_RGTR
, FRST_REG_DT
, LAST_MDFR
, LAST_MDFCN_DT
 )
 VALUES
 (
  FAQ_SN_SEQ.NEXTVAL
, #{faq_ctgry_sn}
, #{faq_qstn}
, #{faq_ans}
, #{sort_no}
, #{expsr_yn}
, #{frst_rgtr}
, SYSDATE
, #{last_mdfr}
, SYSDATE
 )

mapper.update:
/* FAQ 수정 */
UPDATE KB_FAQ_INFO
   SET FAQ_CTGRY     = #{faq_ctgry_sn}
     , FAQ_QSTN      = #{faq_qstn}
     , FAQ_ANS       = #{faq_ans}
     , SORT_NO       = #{sort_no}
     , EXPSR_YN      = #{expsr_yn}
     , LAST_MDFR     = #{last_mdfr}
     , LAST_MDFCN_DT = SYSDATE
 WHERE FAQ_SN        = #{faq_sn}

mapper.delete:
/* FAQ 삭제 */
DELETE
  FROM KB_FAQ_INFO
 WHERE FAQ_SN = #{faq_sn}
