mapper.select:
/* 해당년도 연혁 리스트 한건만 조회 */
SELECT HSTRY_YR
     , ROWNUM ROWNUMBER
  FROM (SELECT HSTRY_YR
          FROM KB_CO_HSTRY_INFO
         GROUP BY HSTRY_YR
         ORDER BY HSTRY_YR DESC
       )
 WHERE ROWNUM BETWEEN #{start} AND #{end}

/* 해당년도 데이터 존재여부 확인 */
SELECT COUNT(*) CNT
  FROM KB_CO_HSTRY_INFO
 WHERE 1 = 1
   AND HSTRY_YR = #{hstry_yr}

mapper.insert:
/* 연혁 등록 */
INSERT INTO KB_CO_HSTRY_INFO
( HSTRY_YR
, HSTRY_LCLSF_SN
, HSTRY_LCLSF_TTL
, HSTRY_LCLSF_SORT_NO
, HSTRY_SCLSF_SN
, HSTRY_SCLSF_TTL
, HSTRY_SCLSF_SORT_NO
, HSTRY_SCLSF_UP_LCLSF_SN
, FRST_RGTR
, FRST_REG_DT
, LAST_MDFR
, LAST_MDFCN_DT
)
VALUES
( #{hstry_yr}
, #{hstry_lclsf_sn}
, #{hstry_lclsf_ttl}
, #{hstry_lclsf_sort_no}
, #{hstry_sclsf_sn}
, #{hstry_sclsf_ttl}
, #{hstry_sclsf_sort_no}
, #{hstry_sclsf_up_lclsf_sn}
, #{frst_rgtr}
, SYSDATE
, #{last_mdfr}
, SYSDATE
)

mapper.delete:
/* 등록 중 오류발생시 해당년도 삭제 */
DELETE
  FROM KB_CO_HSTRY_INFO
 WHERE 1 = 1
  AND HSTRY_YR = #{hstry_yr}
