mapper.select:
SELECT *
FROM KB_STARTERS_APPLY
WHERE SURVEY_NO = #{survey.survey_no}

    AND APPLY_STATUS = #{search.type}

mapper.update:
UPDATE KB_STARTERS_APPLY
SET APPLY_STATUS = #{apply_status}
WHERE APPLY_NO = #{apply_no}

UPDATE KB_STARTERS_SURVEY
