mapper.select:
/* 공지사항 리스트 조회 */ SELECT NOTICE_PAGE.ROWNUMBER , NOTICE_PAGE.NTC_SN , NOTICE_PAGE.NTC_TTL , NOTICE_PAGE.NTC_CN , NOTICE_PAGE.SORT_NO , NOTICE_PAGE.ATCH_FILE_SN , NOTICE_PAGE.EXPSR_YN , NOTICE_PAGE.NTC_YN FROM ( /* 순번 매기기 */ SELECT ROWNUM ROWNUMBER , NOTICE.* FROM ( /* 리스트 조회 */ SELECT NTC_SN , NTC_TTL , NTC_CN , SORT_NO , ATCH_FILE_SN , EXPSR_YN , NTC_YN , ROW_NUMBER() OVER(ORDER BY SORT_NO, NTC_SN DESC) RNUM FROM KB_NTC_INFO WHERE 1=1
AND NTC_TTL LIKE '%'|| #{keyword} ||'%' OR NTC_CN LIKE '%'|| #{keyword} ||'%'
AND NTC_TTL LIKE '%'|| #{keyword} ||'%'
AND NTC_CN LIKE '%'|| #{keyword} ||'%'
ORDER BY NTC_SN ASC ) NOTICE ORDER BY ROWNUMBER DESC ) NOTICE_PAGE WHERE RNUM BETWEEN #{start} AND #{end}

mapper.insert:
 /* 공지사항 파일 저장 */
 INSERT INTO KB_FILE_INFO
 (
  FILE_SN
, FILE_NM
, ORGNL_FILE_NM
, FILE_SZ
, FILE_EXTN
, FILE_PATH
, FRST_RGTR
, FRST_REG_DT
, LAST_MDFR
, LAST_MDFCN_DT
 )
 VALUES
 (
  FILE_SN_SEQ.NEXTVAL
, #{file_nm}
, #{orgnl_file_nm}
, #{file_sz}
, #{file_extn}
, #{file_path}
, #{frst_rgtr}
, SYSDATE
, #{last_mdfr}
, SYSDATE
 )

     /* 저장한 파일 중 제일 큰 것 조회(키 조회) */
     SELECT MAX(FILE_SN) AS FILE_SN
       FROM KB_FILE_INFO

 /* 공지사항 등록 */
 INSERT INTO KB_NTC_INFO
 (
  NTC_SN
, NTC_TTL
, NTC_CN
, ATCH_FILE_SN
, EXPSR_YN
, SORT_NO
, NTC_YN
, FRST_RGTR
, FRST_REG_DT
, LAST_MDFR
, LAST_MDFCN_DT
 )
 VALUES
 (
  NTC_SN_SEQ.NEXTVAL
, #{ntc_ttl}
, #{ntc_cn}
, #{atch_file_sn}
, #{expsr_yn}
, #{sort_no}
, #{ntc_yn}
, #{frst_rgtr}
, SYSDATE
, #{last_mdfr}
, SYSDATE
 )

mapper.delete:
/* 공지사항 파일 삭제 */
DELETE
FROM KB_FILE_INFO
WHERE FILE_SN = #{file_sn}

mapper.update:
/* 공지사항 수정 */
UPDATE KB_NTC_INFO
   SET NTC_TTL       = #{ntc_ttl}
     , NTC_CN        = #{ntc_cn}
     , ATCH_FILE_SN  = #{atch_file_sn}
     , EXPSR_YN      = #{expsr_yn}
     , SORT_NO       = #{sort_no}
     , NTC_YN        = #{ntc_yn}
     , LAST_MDFR     = #{last_mdfr}
     , LAST_MDFCN_DT = SYSDATE
 WHERE NTC_SN        = #{ntc_sn}

mapper.delete:
/* 공지사항 삭제 */
DELETE
  FROM KB_NTC_INFO
 WHERE NTC_SN = #{ntc_sn}
