mapper.select:
/* 팝업 게시글 갯수 조회 */
SELECT COUNT(*)
  FROM KB_POPUP_INFO

mapper.insert:
/* 팝업 등록 */
INSERT INTO KBUSER.KB_POPUP_INFO
 (
  POPUP_SN
 , MNGR_TTL
 , BGNG_YMD
 , BGNG_HR
 , END_YMD
 , END_HR
 , POPUP_LEFT_PSTN
 , POPUP_TOP_PSTN
 , POPUP_CN
 , FRST_RGTR
 , FRST_REG_DT
 , LAST_MDFR
 , LAST_MDFCN_DT
 )
 VALUES
 (
  POPUP_SN_SEQ.NEXTVAL
, #{mngr_ttl}
, #{bgng_ymd}
, #{bgng_hr}
, #{end_ymd}
, #{end_hr}
, #{popup_left_pstn}
, #{popup_top_pstn}
, #{popup_cn}
, #{frst_rgtr}
, SYSDATE
, #{last_mdfr}
, SYSDATE
 )

mapper.update:
/* 팝업 수정 */
UPDATE KB_POPUP_INFO
   SET MNGR_TTL        = #{mngr_ttl}
     , BGNG_YMD        = #{bgng_ymd}
     , BGNG_HR         = #{bgng_hr}
     , END_YMD         = #{end_ymd}
     , END_HR          = #{end_hr}
     , POPUP_LEFT_PSTN = #{popup_left_pstn}
     , POPUP_TOP_PSTN  = #{popup_top_pstn}
     , POPUP_CN        = #{popup_cn}
     , LAST_MDFR       = #{last_mdfr}
     , LAST_MDFCN_DT   = SYSDATE
 WHERE POPUP_SN        = #{popup_sn}

mapper.delete:
/* 팝업 삭제 */
DELETE
  FROM KB_POPUP_INFO
 WHERE POPUP_SN = #{popupId}

mapper.select:
/* 팝업 리스트 조회 (미리보기용) */ SELECT POPUP_SN , MNGR_TTL , TO_CHAR(TO_DATE(BGNG_YMD, 'yyyyMMdd'), 'yyyy-MM-dd') AS BGNG_YMD , TO_CHAR(TO_DATE(BGNG_HR, 'HH24:MI'), 'HH24:MI') AS BGNG_HR , TO_CHAR(TO_DATE(END_YMD, 'yyyyMMdd'), 'yyyy-MM-dd') AS END_YMD , TO_CHAR(TO_DATE(END_HR, 'HH24:MI'), 'HH24:MI') AS END_HR , POPUP_LEFT_PSTN , POPUP_TOP_PSTN , POPUP_CN FROM KB_POPUP_INFO WHERE 1=1
AND NOT POPUP_SN IN (#{popup_sn})
ORDER BY POPUP_SN DESC
