)

mapper.update:
/* 포트폴리오 년도 수정 */
UPDATE KB_PORT_YR_INFO
   SET PORT_YR = #{port_yr}
     , LAST_MDFR = #{last_mdfr}
     , LAST_MDFCN_DT = SYSDATE
 WHERE PORT_YR = #{beforeYr}

mapper.select:
/* 포트폴리오 년도별 리스트 건수 */ SELECT COUNT(PORT_SN) TOTCNT FROM ( SELECT ROWNUM ROWNUMBER , Z.PORT_YR , Z.PORT_SN , Z.BZENTY_NM , Z.INTRO_CN , Z.HMPG_LINK , Z.ATCH_FILE_SN , Z.FILE_NM , Z.FILE_PATH , Z.RNUM FROM ( SELECT A.PORT_YR , A.PORT_SN , A.BZENTY_NM , A.INTRO_CN , A.HMPG_LINK , A.ATCH_FILE_SN , B.FILE_NM , B.FILE_PATH , ROW_NUMBER() OVER(ORDER BY A.PORT_SN DESC) RNUM FROM KB_PORT_INFO A , KB_FILE_INFO B WHERE 1 = 1 AND A.ATCH_FILE_SN = B.FILE_SN(+) AND A.PORT_YR = #{port_yr}
AND A.BZENTY_NM LIKE '%'|| #{keyword} ||'%'
ORDER BY A.PORT_SN ASC ) Z ORDER BY ROWNUMBER DESC )

/* 포트폴리오 년도별 리스트 */ SELECT PORT_YR , PORT_SN , BZENTY_NM , INTRO_CN , HMPG_LINK , ATCH_FILE_SN , FILE_NM , FILE_PATH , RNUM FROM ( SELECT ROWNUM ROWNUMBER , Z.PORT_YR , Z.PORT_SN , Z.BZENTY_NM , Z.INTRO_CN , Z.HMPG_LINK , Z.ATCH_FILE_SN , Z.FILE_NM , Z.FILE_PATH , Z.RNUM FROM ( SELECT A.PORT_YR , A.PORT_SN , A.BZENTY_NM , A.INTRO_CN , A.HMPG_LINK , A.ATCH_FILE_SN , B.FILE_NM , B.FILE_PATH , ROW_NUMBER() OVER(ORDER BY A.PORT_SN DESC) RNUM FROM KB_PORT_INFO A , KB_FILE_INFO B WHERE 1 = 1 AND A.ATCH_FILE_SN = B.FILE_SN(+) AND A.PORT_YR = #{port_yr}
AND A.BZENTY_NM LIKE '%'|| #{keyword} ||'%'
ORDER BY A.PORT_SN ASC ) Z ORDER BY ROWNUMBER DESC ) WHERE RNUM BETWEEN #{start} AND #{end}

mapper.insert:
