  #{port_sn}
, #{port_yr}
, #{bzenty_nm}
, #{intro_cn}
, #{hmpg_link}
, #{atch_file_sn}
, #{sort_no}
, #{frst_rgtr}
, SYSDATE
, #{last_mdfr}
, SYSDATE
)

mapper.select:
/* 포트폴리오 년도별 일련번호 구하기 */
SELECT NVL(MAX(PORT_SN), 0) + 1
  FROM KB_PORT_INFO
 WHERE PORT_YR = #{port_yr}

mapper.insert:
/* 포트폴리오 년도별 첨부파일 등록 */
INSERT INTO KB_FILE_INFO
(
FILE_SN
, FILE_NM
, ORGNL_FILE_NM
, FILE_SZ
, FILE_EXTN
, FILE_PATH
, FRST_RGTR
, FRST_REG_DT
, LAST_MDFR
, LAST_MDFCN_DT
)
VALUES
(
FILE_SN_SEQ.NEXTVAL
, #{file_nm}
, #{orgnl_file_nm}
, #{file_sz}
, #{file_extn}
, #{file_path}
, #{frst_rgtr}
, SYSDATE
, #{last_mdfr}
, SYSDATE
)

    /* 저장한 파일 중 제일 큰 것 조회(키 조회) */
    SELECT MAX(FILE_SN) AS FILE_SN
    FROM KB_FILE_INFO

mapper.delete:
/* 포트폴리오 년도별 리스트 삭제 */
DELETE
FROM KB_PORT_INFO
WHERE 1 = 1
  AND PORT_SN = #{port_sn}
  AND PORT_YR = #{port_yr}

/* 포트폴리오 년도별 전체 삭제 */
DELETE
FROM KB_PORT_INFO
WHERE 1 = 1
  AND PORT_YR = #{port_yr}

/* 포트폴리오 년도 삭제 */
DELETE
FROM KB_PORT_YR_INFO
WHERE 1 = 1
  AND PORT_YR = #{port_yr}

