mapper.select:
/* 관리자 계정 아이디 중복 체크 */
SELECT COUNT(*)
  FROM KB_MNGR_MBR_INFO
 WHERE MNGR_ID = #{memberId}

mapper.insert:
 /* 관리자 계정 추가 */
 INSERT INTO KB_MNGR_MBR_INFO
 (
  MNGR_SN
, MNGR_ID
, MNGR_PSWD
, MNGR_NM
, MNGR_EML
, LAST_CNTN_DT
, FRST_RGTR
, FRST_REG_DT
, LAST_MDFR
, LAST_MDFCN_DT
 )
 VALUES
 (
  MNGR_SN_SEQ.NEXTVAL
, #{mngr_id}
, #{mngr_pswd}
, #{mngr_nm}
, #{mngr_eml}
, #{last_cntn_dt}
, #{frst_rgtr}
, SYSDATE
, #{last_mdfr}
, SYSDATE
 )

mapper.delete:
/* 관리자 계정 삭제 */
DELETE
FROM KB_MNGR_MBR_INFO
WHERE MNGR_SN = #{memberId}
