mapper.insert:
 /* 육성공간 파일 추가 */
 INSERT INTO KB_FILE_INFO
 (
  FILE_SN
, FILE_NM
, ORGNL_FILE_NM
, FILE_SZ
, FILE_EXTN
, FILE_PATH
, FRST_RGTR
, FRST_REG_DT
, LAST_MDFR
, LAST_MDFCN_DT
 )
 VALUES
 (
  FILE_SN_SEQ.NEXTVAL
, #{file_nm}
, #{orgnl_file_nm}
, #{file_sz}
, #{file_extn}
, #{file_path}
, #{frst_rgtr}
, SYSDATE
, #{last_mdfr}
, SYSDATE
 )

     /* 저장한 파일 중 제일 큰 것 조회(키 조회) */
     SELECT MAX(FILE_SN) AS FILE_SN
       FROM KB_FILE_INFO

 /* 육성공간 등록 */
 INSERT INTO KB_PLC_INFO
 (
  PLC_SN
, PLC_NM
, PLC_ADDR
, SORT_NO
, ATCH_FILE_SN1
, ATCH_FILE_SN2
, ATCH_FILE_SN3
, FRST_RGTR
, FRST_REG_DT
, LAST_MDFR
, LAST_MDFCN_DT
 )
 VALUES
 (
   PLC_SN_SEQ.NEXTVAL
, #{plc_nm}
, #{plc_addr}
, #{sort_no}
, #{atch_file_sn1}
, #{atch_file_sn2}
, #{atch_file_sn3}
, #{frst_rgtr}
, SYSDATE
, #{last_mdfr}
, SYSDATE
 )

mapper.update:
/* 육성공간 수정 */
UPDATE KB_PLC_INFO
   SET PLC_NM        = #{plc_nm}
     , PLC_ADDR      = #{plc_addr}
     , SORT_NO       = #{sort_no}
     , ATCH_FILE_SN1 = #{atch_file_sn1}
     , ATCH_FILE_SN2 = #{atch_file_sn2}
     , ATCH_FILE_SN3 = #{atch_file_sn3}
     , LAST_MDFR     = #{last_mdfr}
     , LAST_MDFCN_DT = SYSDATE
 WHERE PLC_SN        = #{plc_sn}

mapper.delete:
/* 육성공간 삭제 */
DELETE
  FROM KB_PLC_INFO
 WHERE PLC_SN = #{plc_sn}

/* 육성공간 파일 삭제 */
DELETE
  FROM KB_FILE_INFO
 WHERE FILE_SN = #{file_sn}

mapper.select:
/* 육성공간 리스트 조회 (미리보기용) */ SELECT PLC.PLC_SN AS PLC_SN , PLC.PLC_NM AS PLC_NM , PLC.PLC_ADDR AS PLC_ADDR , FILES1.FILE_PATH AS PLC_PATH1 , FILES1.FILE_NM AS PLC_FILE_NAME1 , FILES2.FILE_PATH AS PLC_PATH2 , FILES2.FILE_NM AS PLC_FILE_NAME2 , FILES3.FILE_PATH AS PLC_PATH3 , FILES3.FILE_NM AS PLC_FILE_NAME3 FROM KB_PLC_INFO PLC , KB_FILE_INFO FILES1 , KB_FILE_INFO FILES2 , KB_FILE_INFO FILES3 WHERE 1=1
AND NOT PLC_SN IN (#{plc_sn})
AND PLC.ATCH_FILE_SN1 = FILES1.FILE_SN(+) AND PLC.ATCH_FILE_SN2 = FILES2.FILE_SN(+) AND PLC.ATCH_FILE_SN3 = FILES3.FILE_SN(+) ORDER BY PLC.SORT_NO DESC
