mapper.insert:
  /* 국내 프로그램 - 제휴 사례 파일 저장 */
  INSERT INTO KB_FILE_INFO
  (
   FILE_SN
 , FILE_NM
 , ORGNL_FILE_NM
 , FILE_SZ
 , FILE_EXTN
 , FILE_PATH
 , FRST_RGTR
 , FRST_REG_DT
 , LAST_MDFR
 , LAST_MDFCN_DT
  )
 VALUES
 (
  FILE_SN_SEQ.NEXTVAL
, #{file_nm}
, #{orgnl_file_nm}
, #{file_sz}
, #{file_extn}
, #{file_path}
, #{frst_rgtr}
, SYSDATE
, #{last_mdfr}
, SYSDATE
  )

      /* 저장한 파일 중 제일 큰 것 조회(키 조회) */
      SELECT MAX(FILE_SN) AS FILE_SN
      FROM KB_FILE_INFO

 /* 국내 프로그램 - 제휴 사례 등록 */
 INSERT INTO KB_AFFILIATE_INFO
 (
  AFFILIATE_SN
, AFFILIATE_NM
, STARTER_NM
, SRVC_NM
, CTGRY_NM
, EXPSR_YN
, SORT_NO
, ATCH_FILE_SN
, FRST_RGTR
, FRST_REG_DT
, LAST_MDFR
, LAST_MDFCN_DT
 )
 VALUES
 (
  AFFILIATE_SN_SEQ.NEXTVAL
, #{affiliate_nm}
, #{starter_nm}
, #{srvc_nm}
, #{ctgry_nm}
, #{expsr_yn}
, #{sort_no}
, #{atch_file_sn}
, #{frst_rgtr}
, SYSDATE
, #{last_mdfr}
, SYSDATE
 )

mapper.update:
/* 국내 프로그램 - 제휴 사례 수정 */
UPDATE KB_AFFILIATE_INFO
   SET AFFILIATE_NM  = #{affiliate_nm}
     , STARTER_NM    = #{starter_nm}
     , SRVC_NM       = #{srvc_nm}
     , CTGRY_NM      = #{ctgry_nm}
     , EXPSR_YN      = #{expsr_yn}
     , SORT_NO       = #{sort_no}
     , ATCH_FILE_SN  = #{atch_file_sn}
     , LAST_MDFR     = #{last_mdfr}
     , LAST_MDFCN_DT = SYSDATE
 WHERE AFFILIATE_SN  = #{affiliate_sn}

mapper.delete:
/* 국내 프로그램 - 제휴 사례 삭제 */
DELETE
  FROM KB_AFFILIATE_INFO
 WHERE AFFILIATE_SN = #{affiliate_sn}

/* 국내 프로그램 - 제휴 사례 파일 삭제 */
DELETE
  FROM KB_FILE_INFO
 WHERE FILE_SN = #{file_sn}

mapper.select:
/* 메인 비주얼 리스트 조회 (미리보기용) */ SELECT AFFI.AFFILIATE_SN AS AFFILIATE_SN , AFFI.AFFILIATE_NM AS AFFILIATE_NM , AFFI.STARTER_NM AS STARTER_NM , AFFI.SRVC_NM AS SRVC_NM , AFFI.CTGRY_NM AS CTGRY_NM , AFFI.EXPSR_YN AS EXPSR_YN , AFFI.SORT_NO AS SORT_NO , FILES.FILE_NM AS AFFILIATE_FILE_NAME , FILES.FILE_PATH AS AFFILIATE_PATH FROM KB_AFFILIATE_INFO AFFI , KB_FILE_INFO FILES WHERE 1=1 AND AFFI.ATCH_FILE_SN = FILES.FILE_SN(+)
AND NOT AFFI.AFFILIATE_SN IN (#{affiliate_sn})
ORDER BY AFFI.SORT_NO DESC
